import React from "react";
import styled from "styled-components";
import { dataImages } from "../images/images";
import ironMan from "../images/player.png";
import { makeChoice } from "../features/gameLogicSlice";
import { useDispatch, useSelector } from "react-redux";
import Choices from "./Choices";

export default function Player({ choice: choiceFlag }) {
  const { choice } = useSelector((state) => state.gameLogic);
  const dispatch = useDispatch();
  const renderItem = () => {
    return dataImages.map((item, index) => {
      return (
        <div
          className={`${choice === choiceFlag ? "active col-4" : "col-4"}`}
          key={index}
          style={{ width: 45, height: 45 }}
        >
          <button
            onClick={() => {
              dispatch(makeChoice(choiceFlag));
            }}
          >
            <img src={`${dataImages[index]}`} />
          </button>
        </div>
      );
    });
  };
  return (
    <Wrapper>
      <div className=" things">
        <img src={`${dataImages[1]}`} />
      </div>
      <div className=" speech-bubble"></div>
      <img style={{ width: "270px" }} src={`${ironMan}`} />
      <div className="row">
        <Choices className="col-4" />
        <Choices className="col-4" />
        <Choices className="col-4" />
      </div>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  .things {
    position: relative;
    left: 38%;
    width: 100px;
    height: 100px;
    background-color: white;
    border: 5px solid #45d994;
  }
  .speech-bubble {
    position: relative;
    background: #45d994;
    border-radius: 0.4em;
  }
  .speech-bubble:after {
    content: "";
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 0;
    border: 35px solid transparent;
    border-top-color: #45d994;
    border-bottom: 0;
    border-left: 0;
    margin-left: -17.5px;
    margin-bottom: -35px;
  }
  .items {
    width: 70px;
  }
  .active {
    border: 10px solid green;
  }
`;
